import React from "react";
import { useSelector } from "react-redux";
import { useDispatch } from "react-redux";
import { removeFromWishlist, addToWishlist, addToBasket } from "../redux/reducer.tsx";
import currencyFormatter from '../util/currencyFormatter';
import "./plp.scss";

const Plp = (props) => {

  const dispatch = useDispatch()

  const { wishlistItems } = useSelector(
    (state) => state.wishlist
  );

  const isInWishlist = (id) =>{
    if(wishlistItems.some((obj) => obj.id === id))
      return true
    return false
  }

  const { list } = props;

  return (
    <>
      {list.map((product) => (
        // {id, title, image, price} = product
        <div key={product.id} className="plp">
          <div className="plp__name">{product.title}</div>
          <div className="plp__image">
            <img src={product.image} alt={product.description}></img>
          </div>
          <div className="plp__price">{currencyFormatter(product.price)}</div>
          <div className="plp__buttons">
            { isInWishlist(product.id) ?
              <button
                aria-label="remove from wishlist"
                className="removeFromWishlist"
                onClick={() => dispatch(removeFromWishlist(product.id))}
              >
                Wishlisted
              </button> 
              :
              <button
                aria-label="add to wishlist"
                className="button-with-text"
                onClick={() => dispatch(addToWishlist(product))}
              >
                Add to wishlist
              </button>

            }
            <button
              aria-label="add to cart"
              className="button-with-text"
              onClick={() => dispatch(addToBasket(product))}
            >
              Add to cart
            </button>
          </div>
        </div>
      ))}
    </>
  );
};

export default Plp;
