import React, { useEffect, useState } from 'react';
import BasketItem from './basketItem';
import './basket.css'
// quantity
// total

const Basket = props => {

    const { showBasket, items, removeFromBasket, emptyCart, duplicateFound } = props
    const [ show, setShow ]=useState('hidden')

    // Take the last object in items and compare it's id

    // const isDuplucate = (id) => {
    //     if(items.length > 1){
    //         let lastItemId = items[items.length - 1].id
    //         if(id === lastItemId){
    //             items.pop()
    //             return true
    //         }
    //         return false
    //     }
    //     return false
    // }

    useEffect(() => {
        if(duplicateFound){
            console.log("Duplicate found: ", duplicateFound)
        }
    },[duplicateFound])

    useEffect(() =>{
        if(showBasket){
            setShow('show')
        } else {
            setShow('hidden')
        }
    },[showBasket])

    return (
        <>
            <div className={`basket-container ${show}`}>
                <span className='basket-title'>
                    Basket
                </span>
                {!items.length ?
                <div className='nothing-text'>
                    There's nothing here yet
                </div> : 
                <>
                <div className='basket-clear-button'>
                <button onClick={() => emptyCart()}>Clear All</button>
                </div>
                {items.map((item, index) => (
                    <div key={item.id} className='item-container'>
                        <BasketItem
                            // isDuplucate={isDuplucate}
                            item={item}
                            removeFromBasket={removeFromBasket} />
                    </div>
                ))
                }
                </>

            }
            </div>
        </>
    )
}

export default Basket;
