import React, { useEffect, useState } from "react";
import "./basketItem.css";

const BasketItem = (props) => {
  const { item, removeFromBasket } = props;
  const { title, image, price, id } = item;
  const [quantity, setQuantity] = useState(1);

  const reduceQuantity = () => {
    if (quantity===1) {
      removeFromBasket(id)
    } else {
        setQuantity(quantity-1)
    }
  }

  const changeQuantity = (value) => {
    setQuantity(quantity+value)
  }

  useEffect(() => {
    if(quantity < 1){
      removeFromBasket(id)
    }
  },[quantity])

  // isDuplucate(id) ?
  // setQuantity(quantity+1) :
  // console.log("Unique!!")

  const formatter = new Intl.NumberFormat('en-US', {
    style: 'currency',
    currency: 'GBP',
  });

  let fullPrice = formatter.format(price * quantity)


  return (
    <>
      <div className="basketItem-container">
        <div className="basketItem-image">
          <img src={image} alt="product"></img>
        </div>
        <div className="basketItem-info">
          <div className="basketItem-title">{title}</div>
          <div className="basketItem-bottom">
            <div className="basketItem-quantity">
                <button
                  aria-label='reduce quantity'
                  className="quantity-modifier"
                  onClick={() => reduceQuantity()}
                >
                  &mdash;
                </button>
                <input
                  className="quantity-number"
                  type="text"
                  value={quantity}
                  readOnly
                />
                <button
                  aria-label='increase quantity'
                  className="quantity-modifier"
                  onClick={() => setQuantity(quantity+1)}
                >
                  &#xff0b;
                </button>
            </div>
            <div className="basketItem-price">
                {fullPrice}
            </div>
          </div>
        </div>
        <div className="basketItem-right">
          <button onClick={() => removeFromBasket(id)} className="icon-button">
            <i aria-label='remove product' className="fa fa-2x fa-close basketItem-icon"></i>
          </button>
        </div>
      </div>
    </>
  );
};

export default BasketItem;
