import React, { useState, useEffect } from "react";
import "./App.css";
import Plp from "./PLP/plp";
import Wishlist from "./Wishlist/wishlist";
// import Homepage
import Cart from "./Basket/basket";

function App() {
  const [products, setProducts] = useState([]);
  const [wishlistItems, setWishlistItems] = useState([]);
  const [basketItems, setBasketItems] = useState([]);
  const [showWishlist, setShowWishlist] = useState(false);

  const getProducts = async () => {
    const url = `https://fakestoreapi.com/products`;

    try {
      const response = await fetch(url);
      const responseJson = await response.json();
      setProducts(responseJson);
    } catch (error) {
      console.log(error);
    }
  };

  const addToWishlist = (item) => {
    let newWishlist = wishlistItems;
    !newWishlist.some((obj) => obj.id === item.id)
      ? newWishlist.push(item)
      : console.log("Nope");
    setWishlistItems(newWishlist);
  };

  const addToBasket = (item) => {
    let tempBasket = basketItems;
    !tempBasket.some((obj) => obj.id === item.id)
      ? tempBasket.push(item)
      : console.log("Nope");
    setBasketItems(tempBasket);
  };

  const removeFromWishlist = (id) => {
    let filtered = wishlistItems.filter(function (obj) {
      return obj.id !== id;
    });
    setWishlistItems(filtered);
  };

  const removeFromBasket = (id) => {
    let filtered = basketItems.filter(function (obj) {
      return obj.id !== id;
    });
    setBasketItems(filtered);
  };

  const showModal = () => {
    console.log("Setting to: ",!showWishlist)
    setShowWishlist(!showWishlist);
  };

  const emptyCart = () => {
    setBasketItems([]);
  };

  useEffect(() => {
    getProducts();
  });

  return (
    <div className="App">
      <h1>Basic React Checkpoint</h1>
      <span className="top-buttons">
        <button onClick={() => showModal()}>Wishlist</button>
        <button className="basket-button">Basket</button>
      </span>

      <section className="products-container">
        <Wishlist
          showWishlist={showWishlist}
          items={wishlistItems}
          removeFromWishlist={removeFromWishlist}
          addToBasket={addToBasket}
        />
        <Cart
          items={basketItems}
          emptyCart={emptyCart}
          removeFromBasket={removeFromBasket}
          addToBasket={addToBasket}
        />
        <Plp
          list={products}
          addToWishlist={addToWishlist}
          addToBasket={addToBasket}
        />
      </section>
    </div>
  );
}

export default App;
