import React, { useEffect, useState } from "react";
import currencyFormatter from '../util/currencyFormatter'
import "./basketItem.css";

const BasketItem = (props) => {
  const { item, removeFromBasket, isDuplicate } = props;
  const { title, image, price, id, description } = item;
  const [quantity, setQuantity] = useState(1);

  const changeQuantity = (value) => {
    setQuantity(quantity+value)
  }

  useEffect(() => {
    if(quantity < 1){
      removeFromBasket(id)
    }
  },[quantity])

  useEffect(() => {
    if(isDuplicate){
      changeQuantity(1)
    }
  },[isDuplicate])

  let fullPrice = currencyFormatter(price * quantity)


  return (
    <>
      <div className="basketItem-container">
        <div className="basketItem-image">
          <img src={image} alt={description}></img>
        </div>
        <div className="basketItem-info">
          <div className="basketItem-title">{title}</div>
          <div className="basketItem-bottom">
            <div className="basketItem-quantity">
                <button
                  aria-label='reduce quantity'
                  className="quantity-modifier"
                  onClick={() => changeQuantity(-1)}
                >
                  &mdash;
                </button>
                <input
                  className="quantity-number"
                  type="text"
                  value={quantity}
                  readOnly
                />
                <button
                  aria-label='increase quantity'
                  className="quantity-modifier"
                  onClick={() => changeQuantity(1)}
                >
                  &#xff0b;
                </button>
            </div>
            <div className="basketItem-price">
                {fullPrice}
            </div>
          </div>
        </div>
        <div className="basketItem-right">
          <button onClick={() => removeFromBasket(id)} className="icon-button">
            <i aria-label='remove product' className="fa fa-2x fa-close basketItem-icon"/>
          </button>
        </div>
      </div>
    </>
  );
};

export default BasketItem;
