import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { emptyCart } from "../../redux/reducer";
import BasketItem from "./basketItem.tsx";
import "./basket.scss";

const Basket = (props) => {
  const { showBasket } = props;
  const { basketListItems } = useSelector((state) => state.basket)
  const [show, setShow] = useState("hidden");
  const [quantifiedList, setQuantifiedList] = useState([])
  const [uniqueItems, setUniqueItems] = useState([])

  const dispatch = useDispatch()

  useEffect(() => {
    if (showBasket) {
      setShow("show");
    } else {
      setShow("hidden");
    }
  }, [showBasket]);

  useEffect(() => {
    assignBasketListQuantities()
  },[basketListItems])

  const assignBasketListQuantities = () => {
    let tempList = []
    let tempList2 = []
      // count the number of duplicates
      for  (let item of basketListItems) {
        const itemDuplicates = basketListItems.filter(itemCompared => {
            return itemCompared.id === item.id;
        }).length;
        // assign the # of duplicates to each item
        const quantifiedItem = { item: item, quantity: itemDuplicates }
        // add each  unique item to a list that will be displayed in the basket
        if(!tempList.some((QListItem) => QListItem.item.id === quantifiedItem.item.id)){
          tempList.push(quantifiedItem)
        }
      }
      if(!tempList2.some((item) => item.item.id === basketListItems.item.id)){
        tempList2.push(basketListItems.item)
      }
    setQuantifiedList(tempList)
    console.log(tempList2)
  }

  return (
    <>
      <div className={`basket basket--${show}`} data-testid="basket-container">
        <span className="basket-title">
          <h2> Basket </h2>
        </span>
        {!quantifiedList.length ? (
          <div className="nothing-text" data-testid="basket-empty">There's nothing here yet</div>
        ) : (
          <>
            <div className="basket__clear-all-button">
              <button
                aria-label="empty cart"
                className="button-with-text"
                data-testid="empty-basket-button"
                onClick={() => dispatch(emptyCart())}
              >
                Clear All
              </button>
            </div>

            {quantifiedList.map((item) => (
              <div key={item.item.id} className="basket__item-container" data-testid="basket-item-container">
                <BasketItem
                  item={item}
                />
              </div>
            ))}
          </>
        )}
      </div>
    </>
  );
};

export default Basket;
