import React, { useState, useEffect } from "react";
import "./App.css";
import Plp from "./PLP/plp";
import Wishlist from "./Wishlist/wishlist";
import Basket from "./Basket/basket";

// TODO addToBasket needs to increase quantity

function App() {
  const [products, setProducts] = useState([]);
  const [wishlistItems, setWishlistItems] = useState([]);
  const [basketItems, setBasketItems] = useState([]);
  const [showWishlist, setShowWishlist] = useState(false);
  const [showBasket, setShowBasket] = useState(false);
  const [duplicateFound, setDuplicateFound] = useState(null)

  const getProducts = async () => {
    if(!products.length){
      const url = `https://fakestoreapi.com/products`;

      try {
        const response = await fetch(url);
        const responseJson = await response.json();
        setProducts(responseJson);
      } catch (error) {
        console.log(error);
      }
    }
  };

  const addToWishlist = (item) => {
    let tempWishlist = wishlistItems;
    if(!tempWishlist.some((obj) => obj.id === item.id))
      setWishlistItems(tempWishlist => [...tempWishlist, item])
  };

  const addToBasket = (item) => {
    let tempBasket = basketItems;
    // setBasketItems(tempBasket => [...tempBasket, item])
    // duplicateCheck
    !tempBasket.some((obj) => obj.id === item.id)
      ? setBasketItems(tempBasket => [...tempBasket, item])
      // : console.log(item.id, "Already in basket");
      : setDuplicateFound(item.id)
  };

  // const duplicateCheck = () => {
  //   if(basketItems.length > 1){
  //       let lastItemId = basketItems[basketItems.length - 1].id
  //       let filtered = wishlistItems.filter(function (obj) {
  //         return obj.id !== lastItemId;
  //       });
  //       if(id === lastItemId){
  //           items.pop()
  //           return true
  //       }
  //       console.log("No duplicate")
  //   }
  //   console.log("No duplicate")
  // }

  const removeFromWishlist = (id) => {
    let filtered = wishlistItems.filter(function (obj) {
      return obj.id !== id;
    });
    setWishlistItems(filtered);
  };

  const removeFromBasket = (id) => {
    let filtered = basketItems.filter(function (obj) {
      return obj.id !== id;
    });
    setBasketItems(filtered);
  };

  const displayWishlist = () => {
    setShowWishlist(!showWishlist);
  };
  const displayBasket = () => {
    setShowBasket(!showBasket);
  }

  const emptyCart = () => {
    setBasketItems([]);
  };

  const outSideModalClick = () => {
    function handleClickOutside(event) {
      const wishlistElem = document.getElementsByClassName("wishlist-container")[0]
      const basketElem = document.getElementsByClassName("basket-container")[0]
      if(!wishlistElem.contains(event.target)){
        setShowWishlist(false)
      }
      if(!basketElem.contains(event.target)){
        setShowBasket(false)
      }
    }
    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };

  }

  // useEffect(() => {
  //   console.log("Wishlist updated: ", wishlistItems)
  // },[wishlistItems])
  // useEffect(() => {
  //   console.log("Basket updated: ", basketItems)
  // },[basketItems])


  useEffect(() => {
    getProducts();
    outSideModalClick();
    setDuplicateFound(null)
  });

  // const wrapperRef = useRef(null);
  // useOutsideAlerter(wrapperRef);
  return (
    <div className="App">
      <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/css/font-awesome.min.css"></link>
      <div className="heading-static">
        <h1>Basic React Checkpoint</h1>
        <span className="top-buttons">
          <div className="wishlist-button">
              <i onClick={() => displayWishlist()} className="fa fa-2x fa-heart header-icon"></i>
              <div className="icon-counter">
                {wishlistItems.length}
              </div>
          </div>
          <div className="basket-button">
              <i onClick={() => displayBasket()} className="fa fa-2x fa-shopping-cart header-icon"></i>
            {basketItems.length ?
            <div className="icon-counter">
              {basketItems.length}
            </div> :
            null
            }
          </div>
        </span>
      </div>

      <section className="products-container">
        <Wishlist
          // useOutsideAlerter={useOutsideAlerter}
          showWishlist={showWishlist}
          items={wishlistItems}
          removeFromWishlist={removeFromWishlist}
          addToBasket={addToBasket}
        />
        <Basket
          // useOutsideAlerter={useOutsideAlerter}
          duplicateFound={duplicateFound}
          showBasket={showBasket}
          items={basketItems}
          emptyCart={emptyCart}
          removeFromBasket={removeFromBasket}
          addToBasket={addToBasket}
        />
        <Plp
          list={products}
          addToWishlist={addToWishlist}
          addToBasket={addToBasket}
        />
      </section>
    </div>
  );
}

export default App;
