import React from 'react'

const currencyFormatter = (props) => {

  const formatter = new Intl.NumberFormat('en-US', {
    style: 'currency',
    currency: 'GBP',
  });

  const price = formatter.format(props)

  console.log("Formatted price: ", price)

  return price
}

export default currencyFormatter;
