import React, { useEffect, useState } from 'react';
import BasketItem from './basketItem';
import './basket.css'
// quantity
// total

const Basket = props => {

    const { showBasket, items, removeFromBasket, addToBasket, emptyCart } = props
    const [ show, setShow ]=useState('hidden')

    // Take the last object in items and compare it's id

    // let addQuantity = false

    // const checkForDuplicates = () => {
    //     if(items.length > 1){
    //         let lastItemId = items[items.length - 1].id
    //         items.forEach(item => {
    //             item.id === lastItemId
    //             addQuantity = true
    //         });
    //         items.some((obj)  => obj.id === lastItemId)

    //     }

    // }

    useEffect(() =>{
        if(showBasket){
            setShow('show')
        } else {
            setShow('hidden')
        }
    },[showBasket])

    return (
        <>
            <div className={`basket-container ${show}`}>
                <span className='basket-title'>
                    Basket
                </span>
                {!items.length ?
                <div className='nothing-text'>
                    There's nothing here yet
                </div> : 
                <>
                <div className='basket-clear-button'>
                <button onClick={() => emptyCart()}>Clear All</button>
                </div>
                {items.map((item, index) => (
                    <div key={item.id} className='item-container'>
                        <BasketItem
                            item={item}
                            removeFromBasket={removeFromBasket}
                            addToBasket={addToBasket} />
                    </div>
                ))
                }
                </>

            }
            </div>
        </>
    )
}

export default Basket;
