import { createSlice } from '@reduxjs/toolkit'

export const wishlistReducerSlice = createSlice({
    name: 'wishlistReducer',
    initialState: { wishlistItems: [] },
    reducers: {
        addToWishlist: (state, action) => {
            let tempWishlist = state.wishlistItems;
            if (!tempWishlist.some((obj) => obj.id === action.payload.id))
                state.wishlistItems = [...tempWishlist, action.payload]
        },
        removeFromWishlist: (state, action) => {
        let filtered = state.wishlistItems.filter((obj) => {
            return obj.id !== action.payload;
          });
          state.wishlistItems = filtered
        }

    }
})

export const { addToWishlist, removeFromWishlist } = wishlistReducerSlice.actions

export default wishlistReducerSlice.reducer
