import React, { useEffect, useState } from "react";
import { useDispatch } from "react-redux";
import { addToBasket, removeFromBasket } from "../redux/counter";
import currencyFormatter from '../util/currencyFormatter';
import "./basketItem.scss";

const BasketItem = (props) => {
  const { item, quantity } = props;
  const { title, image, price, id, description } = item;
  // const [quantity, setQuantity] = useState(1);

  const dispatch = useDispatch()

  const changeQuantity = (value) => {
    if(value > 0){
      dispatch(addToBasket(item))
    }
  }

  useEffect(() => {
    if(quantity < 1){
      dispatch(removeFromBasket(id))
    }
  },[quantity])

  // useEffect(() => {
  //   if(isDuplicate){
  //     changeQuantity(1)
  //   }
  // },[isDuplicate])

  let fullPrice = currencyFormatter(price * quantity)


  return (
      <div className="basketItem">
        <div className="basketItem__image">
          <img src={image} alt={description}></img>
        </div>
        <div className="basketItem__info">
          <div className="basketItem__title">{title}</div>
          <div className="basketItem__bottom">
            <div className="basketItem__quantity">
                <button
                  aria-label='reduce quantity'
                  className="basketItem__quantityModifier"
                  onClick={() => changeQuantity(-1)}
                >
                  &mdash;
                </button>
                <input
                  className="basketItem__quantityNumber"
                  type="text"
                  value={quantity}
                  readOnly
                />
                <button
                  aria-label='increase quantity'
                  className="basketItem__quantityModifier"
                  onClick={() => changeQuantity(1)}
                >
                  &#xff0b;
                </button>
            </div>
            <div className="basketItem__price">
                {fullPrice}
            </div>
          </div>
        </div>
        <div className="basketItem__right">
          <button onClick={() => dispatch(removeFromBasket(id))} className="icon-button">
            <i aria-label='remove product' className="fa fa-2x fa-close basketItem__icon"/>
          </button>
        </div>
      </div>
  );
};

export default BasketItem;
