import React from "react";
import currencyFormatter from '../util/currencyFormatter';
import "./wishlistItem.css";

const WishlistItem = (props) => {
  const { removeFromWishlist, item, addToBasket } = props;
  const { title, image, price, id, description } = item;

  const formatter = new Intl.NumberFormat("en-US", {
    style: "currency",
    currency: "GBP",
  });

  let formattedPrice = formatter.format(price);

  return (
    <div className="wishlistItem-container">
      <div className="wishlistItem-image">
        <img src={image} alt={description}></img>
      </div>
      <div className="wishlistItem-info">
        <div className="wishlistItem-title">{title}</div>
        <div className="wishlistItem-price">{currencyFormatter(price)}</div>
      </div>
      <div className="wishlistItem-right">
        <button onClick={() => removeFromWishlist(id)} className="icon-button">
          <i
            aria-label="remove product"
            className="fa fa-2x fa-close wishlistItem-icon"
          />
        </button>
        <button onClick={() => addToBasket(item)} className="icon-button">
          <i
            aria-label="shopping cart"
            className="fa fa-2x fa-shopping-cart wishlistItem-icon"
          />
        </button>
      </div>
    </div>
  );
};

export default WishlistItem;
