import React from 'react';
import './plp.css';

const Plp = props => {


  const formatter = new Intl.NumberFormat('en-US', {
    style: 'currency',
    currency: 'GBP',
  });

  const getPrice = (price) => {
    return formatter.format(price)
  }

    const { addToWishlist, addToBasket } = props
    return (
		<>
			{props.list.map((product, index) => (
                <div key={product.id} className='plp-card'>
                    <div aria-label={product.title} className='name-container'>
                        {product.title}
                    </div>
                    <div className='image-container'>
                        <img src={product.image} alt='product'></img>
                    </div>
                    <div className='price-container'>
                        {getPrice(product.price)}
                    </div>
                    <div className='plp-buttons'>
                        <button aria-label='add to cart' className='button-with-text' onClick={() => addToBasket(product)}>
                            Add to cart
                        </button>
                        <button aria-label='add to wishlist' className='button-with-text' onClick={() => addToWishlist(product)}>
                            Add to wishlist
                        </button>
                    </div>
                </div>
			))}
		</>
    )
}

export default Plp;
