import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { emptyCart } from "../redux/counter";
import BasketItem from "./basketItem";
import "./basket.scss";

// I think there should be a much better way to flag when an items qutity needs to be increased
// I don't like the idea of creating an additional prop just to flag an external quantity increase
// nothing I tried within the items.map() worked how I hoped

const Basket = (props) => {
  const { showBasket } = props;
  const { basketListItems } = useSelector((state) => state.wishlist)
  const [show, setShow] = useState("hidden");
  const [quantifiedList, setQuantifiedList] = useState([])

  const dispatch = useDispatch()

  useEffect(() => {
    if (showBasket) {
      setShow("show");
    } else {
      setShow("hidden");
    }
  }, [showBasket]);

  useEffect(() => {
    basketListItems.length ?
    assignBasketListQuantities() :
    setQuantifiedList([])
  },[basketListItems])

  const assignBasketListQuantities = () => {
    setQuantifiedList(basketListItems)
    console.log(" starting quantifiedList: ",quantifiedList)
    for  (const item of basketListItems) {
      console.log("Checking item: ", item.id)
      const itemDuplicates = basketListItems.filter(itemCompared => {
          return itemCompared.id === item.id;
      }).length;
      const quantifiedItem = { item: item, quantity: itemDuplicates }
      if(!quantifiedList.some((QListItem) => QListItem.item.id === quantifiedItem.item.id)){
        // console.log("Adding new item: ",quantifiedItem," to:")
        setQuantifiedList([...quantifiedList, quantifiedItem])
        // setBasketItems((tempBasket) => [...tempBasket, item])
      } else {
        // console.log("Duplicated item, current quantifiedList: ",quantifiedList)
        // console.log("basketListItems: ",basketListItems)
        for (const item of quantifiedList){
          if(item.item.id === quantifiedItem.item.id){
            item.quantity = itemDuplicates
          }
        }
      }
    }
    console.log(" ending quantifiedList: ",quantifiedList)
  }

  return (
    <>
      <div className={`basket basket--${show}`}>
        <span className="basket-title">
          <h2> Basket </h2>
        </span>
        {!quantifiedList.length ? (
          <div className="nothing-text">There's nothing here yet</div>
        ) : (
          <>
            <div className="basket__clear-all-button">
              <button
                aria-label="empty cart"
                className="button-with-text"
                onClick={() => dispatch(emptyCart())}
              >
                Clear All
              </button>
            </div>
            {quantifiedList.map((item) => (
              <div key={item.item.id} className="basket__item-container">
                <BasketItem
                  item={item.item}
                  quantity={item.quantity}
                />
              </div>
            ))}
          </>
        )}
      </div>
    </>
  );
};

export default Basket;
