import React, { useState, useEffect } from "react";
import "./App.css";
import { getProducts } from './util/fetchData';
import LoadingSpinner from "./util/loadingSpinner";
import Navbar from "./Navbar/navbar";
import Plp from "./PLP/plp";
import Wishlist from "./Wishlist/wishlist";
import Basket from "./Basket/basket";
import { useSelector } from "react-redux";


function App() {
  const [isLoading, setIsLoading] = useState(true)
  const [errorMessage, setErrorMessage] = useState('')
  const [products, setProducts] = useState([]);

  const [showWishlist, setShowWishlist] = useState(false);
  const [showBasket, setShowBasket] = useState(false);

  const [duplicateItemFound, setDuplicateItemFound] = useState(null);

  const { wishlistItems, basketListItems } = useSelector((state) =>  state.wishlist);



  useEffect(() => {
    setIsLoading(true)
    getProducts()
    .then(response => response.json())
    .then(data => {
      setProducts(data)
      setIsLoading(false)
    })
    .catch(() => {
      setErrorMessage("Unable to fetch user list");
      setIsLoading(false);
    })
    // .catch(error => console.log(error))
  },[])


  const displayWishlist = () => {
    setShowWishlist(!showWishlist);
  };
  const displayBasket = () => {
    setShowBasket(!showBasket);
  };

  const outSideModalClick = () => {
    const handleClickOutside = (event) => {
      const wishlistElem = document.getElementsByClassName("wishlist")[0];
      const basketElem = document.getElementsByClassName("basket")[0];
      if (!wishlistElem.contains(event.target)) {
        setShowWishlist(false);
      }
      if (!basketElem.contains(event.target)) {
        setShowBasket(false);
      }
    }
    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  };

  useEffect(() => {
    getProducts();
    outSideModalClick();
  },[]);

  return (
    <div className="App">
      <link
        rel="stylesheet"
        href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/css/font-awesome.min.css"
      ></link>
      <Navbar />

    {
      isLoading ?
        <LoadingSpinner/>
      :
      <section className="products-container">
        <Plp
          list={products}
          // addToBasket={addToBasket}
        />
      </section>
      }
    </div>
  );
}

export default App;
