import React, { useEffect, useState } from "react";
import "./basketItem.css";

const BasketItem = (props) => {
  const { item, removeFromBasket, addToBasket } = props;
  const { title, image, price, id } = item;
  const [quantity, setQuantity] = useState(1);
  useEffect(() => {
    if (!quantity) {
      console.log("removing basketItem: ", id, " quantity: ", quantity);
      // removeFromBasket(id)
    }
  });

  return (
    <>
      <link
        rel="stylesheet"
        href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/css/font-awesome.min.css"
      ></link>
      <div className="basketItem-container">
        <div className="basketItem-image">
          <img src={image} alt="product"></img>
        </div>
        <div className="basketItem-info">
          <div className="basketItem-title">{title}</div>
          <div className="basketItem-price">
            <div className="basketItem-quantity">
              <div className="quantity-input">
                <button
                  className="quantity-input__modifier quantity-input__modifier--left"
                  onClick={() => setQuantity(quantity-1)}
                >
                  &mdash;
                </button>
                <input
                  className="quantity-input__screen"
                  type="text"
                //   defaultValue={quantity}
                  value={quantity}
                  onChange={() => setQuantity}
                />
                <button
                  className="quantity-input__modifier quantity-input__modifier--right"
                  onClick={() => setQuantity(quantity+1)}
                >
                  &#xff0b;
                </button>
              </div>
              quantity
            </div>
            {price}
          </div>
        </div>
        <div className="basketItem-buttons">
          <i onClick={() => removeFromBasket(id)} className="fa fa-close"></i>
          <i
            onClick={() => addToBasket(id)}
            className="fa fa-shopping-cart"
          ></i>
        </div>
      </div>
    </>
  );
};

export default BasketItem;
