import React, { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { emptyCart } from "../redux/counter";
import BasketItem from "./basketItem";
import "./basket.scss";

// I think there should be a much better way to flag when an items qutity needs to be increased
// I don't like the idea of creating an additional prop just to flag an external quantity increase
// nothing I tried within the items.map() worked how I hoped

const Basket = (props) => {
  const { showBasket, duplicateFound } = props;
  const { basketListItems } = useSelector((state) => state.wishlist)
  const [show, setShow] = useState("hidden");
  const [quantifiedList, setQuantifiedList] = useState([])

  const dispatch = useDispatch()

  useEffect(() => {
    if (showBasket) {
      setShow("show");
    } else {
      setShow("hidden");
    }
  }, [showBasket]);

  useEffect(() => {
    assignBasketListQuantities()
  },[basketListItems])

  const assignBasketListQuantities = () => {
    for  (const item of basketListItems) {
      const itemQuantity = basketListItems.filter(arr => {
          return arr.id === item.id;
      }).length;
      const quantifiedItem = { item: item, quantity: itemQuantity}
      if(!quantifiedList.some((listItem) => listItem.item.id === quantifiedItem.item.id)){
        setQuantifiedList([...quantifiedList, quantifiedItem])
        console.log("adding new item: ",quantifiedItem.item)
      } else {
        for (const item of quantifiedList){
          if(item.item.id === quantifiedItem.item.id){
            item.quantity = itemQuantity
          }
        }
      }
    }
  }

  return (
    <>
      <div className={`basket basket--${show}`}>
        <span className="basket-title">
          <h2> Basket </h2>
        </span>
        {!quantifiedList.length ? (
          <div className="nothing-text">There's nothing here yet</div>
        ) : (
          <>
            <div className="basket__clear-all-button">
              <button
                aria-label="empty cart"
                className="button-with-text"
                onClick={() => dispatch(emptyCart())}
              >
                Clear All
              </button>
            </div>
            {quantifiedList.map((item) => (
              <div key={item.item.id} className="basket__item-container">
                <BasketItem
                  item={item.item}
                  quantity={item.quantity}
                />
              </div>
            ))}
          </>
        )}
      </div>
    </>
  );
};

export default Basket;
