import React, { useEffect, useState } from "react";
import WishlistItem from "./wishlistItem";
import "./wishlist.scss";

const Wishlist = (props) => {
  const { showWishlist, items, removeFromWishlist, addToBasket } = props;
  const [show, setShow] = useState("hidden");
  useEffect(() => {
    if (showWishlist) {
      setShow("show");
    } else {
      setShow("hidden");
    }
  }, [showWishlist]);

  return (
    <div className={`wishlist--${show}`}>
      <span className="wishlist-title">
        <h2>Wishlist</h2>
      </span>
      {!items.length ? (
        <div className="nothing-text">There's nothing here yet</div>
      ) : (
        items.map((item) => (
          <div key={item.id} className="wishlist__item-container">
            <WishlistItem
              item={item}
              removeFromWishlist={removeFromWishlist}
              addToBasket={addToBasket}
            />
          </div>
        ))
      )}
    </div>
  );
};

export default Wishlist;
