import React, { useEffect, useState, useRef } from 'react';
import BasketItem from './basketItem';
import './basket.css'
// quantity
// total

const Basket = props => {
    const { showBasket, items, removeFromBasket, addToBasket, emptyCart, useOutsideAlerter } = props
    const [ show, setShow ]=useState('hidden')
    useEffect(() =>{
        if(showBasket){
            setShow('show')
        } else {
            setShow('hidden')
        }
    },[showBasket])
    // const wrapperRef = useRef(null);
    // useOutsideAlerter(wrapperRef);
    return (
        <>
            <div ref={wrapperRef} className={`basket-container ${show}`}>
                <span className='basket-title'>
                    Basket
                </span>
                {!items.length ?
                <div className='nothing-text'>
                    There's nothing here yet
                </div> : 
                <>
                <div className='basket-clear-button'>
                <button onClick={() => emptyCart()}>Clear All</button>
                </div>
                {items.map((item, index) => (
                    <div key={item.id} className='item-container'>
                        <BasketItem
                            item={item}
                            removeFromBasket={removeFromBasket}
                            addToBasket={addToBasket} />
                    </div>
                ))
                }
                </>

            }
            </div>
        </>
    )
}

export default Basket;
