import React, { useEffect, useState } from "react";
import BasketItem from "./basketItem";
import "./basket.css";

// I think there should be a much better way to flag when an items qutity needs to be increased
// I don't like the idea of creating an additional prop just to flag an external quantity increase
// nothing I tried within the items.map() worked how I hoped

const Basket = (props) => {
  const { showBasket, items, removeFromBasket, emptyCart, duplicateFound } =
    props;
  const [show, setShow] = useState("hidden");

  useEffect(() => {
    if (showBasket) {
      setShow("show");
    } else {
      setShow("hidden");
    }
  }, [showBasket]);

  const stateYourNameCuz = (id) => {
    console.log(id, " init")
  }

  const isDuplicate = (id) => {
    return duplicateFound === id
  }

  return (
    <>
      <div className={`basket-container ${show}`}>
        <span className="basket-title">
          <h2> Basket </h2>
        </span>
        {!items.length ? (
          <div className="nothing-text">There's nothing here yet</div>
        ) : (
          <>
            <div className="basket-clear-all-button">
              <button
                aria-label="empty cart"
                className="button-with-text"
                onClick={() => emptyCart()}
              >
                Clear All
              </button>
            </div>
            {items.map((item, index) => (
              <div key={item.id} className="item-container">
                <BasketItem
                  duplicateFound={isDuplicate(item.id)}
                  item={item}
                  removeFromBasket={removeFromBasket}
                />
              </div>
            ))}
          </>
        )}
      </div>
    </>
  );
};

export default Basket;
