import { createSlice } from '@reduxjs/toolkit'

// interface basketItem {
//     title: string
//     image: string
//     price: number
//     id: number
//     description: string
// }

// type basketListItems = Array<basketItem>

export const basketReducerSlice = createSlice({
    name: 'basketReducer',
    // initialState: basketListItems = [],
    initialState: { basketListItems: [] },
    reducers: {
        addToBasket: (state, action) => {
            let tempBasket = state.basketListItems;
                state.basketListItems = [...tempBasket, action.payload]
        },
        removeFromBasket: (state, action) =>  {
            let filtered = state.basketListItems.filter((obj) => {
              return obj.id !== action.payload;
            });
            state.basketListItems = filtered
        },
        removeSingleQuantity: (state, action) => {
          let index
          for(const item of state.basketListItems){
            if(item.id === action.payload){
              index = state.basketListItems.indexOf(item);
              state.basketListItems.splice(index, 1);
              return
            }
          }
        },
        emptyCart: (state) => {
            state.basketListItems = []
        }

    }
})

export const { addToBasket, removeFromBasket, removeSingleQuantity, emptyCart} = basketReducerSlice.actions

export default basketReducerSlice.reducer
