import { createSlice } from '@reduxjs/toolkit'

export const counterSlice = createSlice({
  name: 'counter',
  initialState: {
    // known as count
    count: 0,
    wishlistItems: []
  },
  reducers: {
    increment: state => {
      state.count += 1
    },
    decrement: state => {
      state.count -= 1
    },
    incrementByAmount: (state, action) => {
      state.count += action.payload
    },
    addToWishlist: (state, action) => {
        let tempWishlist = state.wishlistItems;
        if (!tempWishlist.some((obj) => obj.id === action.payload.id))
            state.wishlistItems = [...tempWishlist, action.payload]
    },
    removeFromWishlist: (state, action) => {
    let filtered = state.wishlistItems.filter((obj) => {
        return obj.id !== action.payload;
      });
      state.wishlistItems = filtered
    }
  }
})

// Action creators are generated for each case reducer function
export const { increment, decrement, incrementByAmount, addToWishlist, removeFromWishlist } = counterSlice.actions

export default counterSlice.reducer
