import React, { useState, useEffect } from "react";
import "./App.css";
import { getProducts } from './util/fetchData';
import Plp from "./PLP/plp";
import Wishlist from "./Wishlist/wishlist";
import Basket from "./Basket/basket";
import { useDispatch, useSelector } from "react-redux";


// Given another chance or more time I would:
//  -fix the quantity increase when adding to basket
//  -add a mobile view
//  -fix the css naming convention

// The user can increase a products quantity by adding it to the basket once
// They they have to add a different product so that the duplicateItemFound hook updates
// For some reason seeting the hook to null at  the start of the  addToBasket function doesn't
// change the hooks value or register as an update


//  HINDSIGHT
// Should have put some of the  hooks in the app into the 
// wishlist and basket  respectively



function App() {
  const [isLoading, setIsLoading] = useState(true)
  const [products, setProducts] = useState([]);
  const [wishlistItems, setWishlistItems] = useState([]);
  const [basketItems, setBasketItems] = useState([]);
  const [showWishlist, setShowWishlist] = useState(false);
  const [showBasket, setShowBasket] = useState(false);
  const [duplicateItemFound, setDuplicateItemFound] = useState(null);

  const { quantity } = useSelector(state =>  state.quantity);
  const dispatch = useDispatch()


  useEffect(() => { 
    setIsLoading(true)
    getProducts()
    .then(response => response.json())
    .then(data => {
      setProducts(data)
      setIsLoading(false)
    })
    .catch(error => console.log(error))
  },[])

  const addToWishlist = (item) => {
    let tempWishlist = wishlistItems;
    if (!tempWishlist.some((obj) => obj.id === item.id))
      setWishlistItems((tempWishlist) => [...tempWishlist, item]);
  };

  const addToBasket = (item) => {
    setDuplicateItemFound(null);
    let tempBasket = basketItems;
    !tempBasket.some((obj) => obj.id === item.id)
      ? setBasketItems((tempBasket) => [...tempBasket, item])
      : setDuplicateItemFound(item.id);
  };

  const removeFromWishlist = (id) => {
    let filtered = wishlistItems.filter((obj) => {
      return obj.id !== id;
    });
    setWishlistItems(filtered);
  };

  const removeFromBasket = (id) => {
    let filtered = basketItems.filter((obj) => {
      return obj.id !== id;
    });
    setBasketItems(filtered);
  };

  const displayWishlist = () => {
    setShowWishlist(!showWishlist);
  };
  const displayBasket = () => {
    setShowBasket(!showBasket);
  };

  const emptyCart = () => {
    setBasketItems([]);
  };

  const outSideModalClick = () => {
    const handleClickOutside = (event) => {
      const wishlistElem = document.getElementsByClassName("wishlist")[0];
      const basketElem = document.getElementsByClassName("basket")[0];
      if (!wishlistElem.contains(event.target)) {
        setShowWishlist(false);
      }
      if (!basketElem.contains(event.target)) {
        setShowBasket(false);
      }
    }
    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  };

  useEffect(() => {
    getProducts();
    outSideModalClick();
  },[]);

  return (
    <div className="App">
      <link
        rel="stylesheet"
        href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/css/font-awesome.min.css"
      ></link>
      <header className="heading">
        <h1>Basic React Checkpoint</h1>
        <span className="top-buttons">
          <div className="wishlist-button">
            <button onClick={() => displayWishlist()} className="icon-button">
              <i
                aria-label="wish list"
                className="fa fa-2x fa-heart header-icon"
              />
            </button>
            {wishlistItems.length ? (
              <div className="icon-counter">{wishlistItems.length}</div>
            ) : null}
          </div>
          <div className="basket-button">
            <button onClick={() => displayBasket()} className="icon-button">
              <i
                aria-label="shopping cart"
                className="fa fa-2x fa-shopping-cart header-icon"
              />
            </button>
            {basketItems.length ? (
              <div className="icon-counter">{basketItems.length}</div>
            ) : null}
          </div>
        </span>
        <Wishlist
          showWishlist={showWishlist}
          items={wishlistItems}
          removeFromWishlist={removeFromWishlist}
          addToBasket={addToBasket}
        />
        <Basket
          duplicateFound={duplicateItemFound}
          showBasket={showBasket}
          items={basketItems}
          emptyCart={emptyCart}
          removeFromBasket={removeFromBasket}
          addToBasket={addToBasket}
        />
      </header>

    {
      isLoading ? 
      <div> 
        Hello
      </div>
      :

      <section className="products-container">
        <Plp
          list={products}
          addToWishlist={addToWishlist}
          addToBasket={addToBasket}
        />
      </section>
      }
    </div>
  );
}

export default App;
