import { createSlice } from '@reduxjs/toolkit'

export const counterSlice = createSlice({
  name: 'counter',
  initialState: {
    // known as count
    quantity: 0,
    wishlistItems: []
  },
  reducers: {
    increment: state => {
      // Redux Toolkit allows us to write "mutating" logic in reducers. It
      // doesn't actually mutate the state because it uses the Immer library,
      // which detects changes to a "draft state" and produces a brand new
      // immutable state based off those changes
      state.quantity += 1
    },
    decrement: state => {
      state.quantity -= 1
    },
    incrementByAmount: (state, action) => {
      state.quantity += action.payload
    },
    addToWishlist: (state, action) => {
        let tempWishlist = state.wishlistItems;
        if (!tempWishlist.some((obj) => obj.id === action.payload.item.id))
            state.wishlistItems = [...tempWishlist, action.payload.item]
        //   setWishlistItems((tempWishlist) => [...tempWishlist, item]);
    }
  }
})

// Action creators are generated for each case reducer function
export const { increment, decrement, incrementByAmount, addToWishlist } = counterSlice.actions

export default counterSlice.reducer
