import React from "react";
import currencyFormatter from '../util/currencyFormatter';
import "./plp.scss";

const Plp = (props) => {

  const { addToWishlist, addToBasket, list } = props;

  return (
    <>
      {list.map((product) => (
        // {id, title, image, price} = product
        <div key={product.id} className="plp">
          <div className="plp__name">{product.title}</div>
          <div className="plp__image">
            <img src={product.image} alt={product.description}></img>
          </div>
          <div className="plp__price">{currencyFormatter(product.price)}</div>
          <div className="plp__buttons">
            <button
              aria-label="add to wishlist"
              className="button-with-text"
              onClick={() => addToWishlist(product)}
            >
              Add to wishlist
            </button>
            <button
              aria-label="add to cart"
              className="button-with-text"
              onClick={() => addToBasket(product)}
            >
              Add to cart
            </button>
          </div>
        </div>
      ))}
    </>
  );
};

export default Plp;
