import React, { useState } from "react";
import "./basketItem.css";

const BasketItem = (props) => {
  const { item, removeFromBasket, addToBasket } = props;
  const { title, image, price, id } = item;
  const [quantity, setQuantity] = useState(1)
  if(!quantity){
    console.log("removing basketItem: ",id, " quantity: ", quantity)
    // removeFromBasket(id)
  }

  return (
    <>
      <link
        rel="stylesheet"
        href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/css/font-awesome.min.css"
      ></link>
      <div className="basketItem-container">
        <div className="basketItem-image">
          <img src={image} alt="product"></img>
        </div>
        <div className="basketItem-info">
          <div className="basketItem-title">{title}</div>
          <div className="basketItem-price">
            <div className="basketItem-quantity">
              <form id="myform" method="POST" class="quantity" action="#">
                <input
                  type="button"
                  value="-"
                  class="qtyminus minus"
                  field="quantity"
                  onClick={() => setQuantity(quantity - 1)}
                />
                <input type="text" name="quantity" value={quantity} class="qty" />
                <input
                  type="button"
                  value="+"
                  class="qtyplus plus"
                  field="quantity"
                  onClick={() => setQuantity(quantity + 1)}
                />
              </form>
              quantity
            </div>
            {price}
          </div>
        </div>
        <div className="basketItem-buttons">
          <i onClick={() => removeFromBasket(id)} class="fa fa-close"></i>
          <i onClick={() => addToBasket(id)} class="fa fa-shopping-cart"></i>
        </div>
      </div>
    </>
  );
};

export default BasketItem;
